function stripTags(s, n) {
    return s.replace(/<.*?>/ig, "").split(/\s+/).slice(0, n - 1).join(" ")
}

function readmore(a) {
      var p = document.getElementById(a),
        imgtag = "",
		ifrtag = "",
        ifrsrc = "",
        ifrtb = -1;
img = p.getElementsByTagName("img");
		ifr = p.getElementsByTagName("iframe");

      for (var i = 0; i < ifr.length; i++) {
        ifrsrc = ifr[i].src;
        if (ifrsrc.indexOf("//www.youtube.com/embed/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//player.vimeo.com/video/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//www.dailymotion.com/embed/video/") != -1) {
            ifrtb = i;
            break
        }
    }
    if (ifrtb != -1) ifrtag = '<div class="entry-video"><iframe width="750" height="400" src="' + ifrsrc + '?vq=medium&rel=0" frameborder="0" allowfullscreen></iframe></div>';
    else if (img.length >= 1) imgtag = '<div class="post-image"><a href="' + y + '"><img width="750" height="400"  class="thumb" src="' + img[0].src + '" /></a></div>';
    else imgtag = '';
    p.innerHTML = ifrtag +'<a href="' + y + '"><div class="large_thumb thumb_hover"><div class="img_wrapper">' + imgtag + "</div></a>" + '<h3 class="post_item_title"><a rel="bookmark" href="' + y + '">' + x + "</a></h3></div>" + '<div class="post_meta"><span class="user">by <span style="color: #E74C3C;">' + z + '</span></span> <span class="time">' + t + "</span></div>" + '<div class="post_item_inner"><p>' + stripTags(p.innerHTML, 20) + '...</p></span><div class="clear"></div></div>'
};
